mapper.update:
update apiresponse
set apiResponse_type = #{apiResponse_type,jdbcType=VARCHAR},
  apiResponse_message = #{apiResponse_message,jdbcType=VARCHAR}
where apiResponse_code = #{apiResponse_code,jdbcType=INTEGER}

mapper.select:
select apiResponse_code, apiResponse_type, apiResponse_message
from apiresponse
where apiResponse_code = #{apiResponse_code,jdbcType=INTEGER}
