mapper.update:
update inventories
set inventories_quantity = #{inventories_quantity,jdbcType=INTEGER},
  pet_id = #{pet_id,jdbcType=INTEGER}
where inventories_id = #{inventories_id,jdbcType=INTEGER}

mapper.select:
select inventories_id, inventories_quantity, pet_id
from inventories
where inventories_id = #{inventories_id,jdbcType=INTEGER}
