mapper.update:
update pet
set category_id = #{category_id,jdbcType=INTEGER},
  pet_name = #{pet_name,jdbcType=VARCHAR},
  pet_price = #{pet_price,jdbcType=DECIMAL},
  pet_status = #{pet_status,jdbcType=VARCHAR}
where pet_id = #{pet_id,jdbcType=INTEGER}

mapper.select:
select pet_id, category_id, pet_name, pet_price, pet_status
from pet
where pet_id = #{pet_id,jdbcType=INTEGER}
