mapper.insert:
insert into photo (photo_id, photo_name, pet_id
  )
values (#{photo_id,jdbcType=INTEGER}, #{photo_name,jdbcType=VARCHAR}, #{pet_id,jdbcType=INTEGER}
  )

mapper.update:
update photo
set photo_name = #{photo_name,jdbcType=VARCHAR},
  pet_id = #{pet_id,jdbcType=INTEGER}
where photo_id = #{photo_id,jdbcType=INTEGER}

mapper.select:
select photo_id, photo_name, pet_id
from photo
where photo_id = #{photo_id,jdbcType=INTEGER}
