mapper.insert:
insert into tag (tag_id, tag_name, pet_id
  )
values (#{tag_id,jdbcType=INTEGER}, #{tag_name,jdbcType=VARCHAR}, #{pet_id,jdbcType=INTEGER}
  )

mapper.update:
update tag
set tag_name = #{tag_name,jdbcType=VARCHAR},
  pet_id = #{pet_id,jdbcType=INTEGER}
where tag_id = #{tag_id,jdbcType=INTEGER}

mapper.select:
select tag_id, tag_name, pet_id
from tag
where tag_id = #{tag_id,jdbcType=INTEGER}
